Shapes.Script:

/**
 * Takes RGB values (each between 0 and 255) and returns the corresponding HSL values (H between 0 and 360, S and L each between 0 and 100).
 * Based on http://stackoverflow.com/a/9493060
 */
collection.rgbToHsl = function(r, g, b) {
	var max, min, h, s, l;

	r = r / 255;
	g = g / 255;
	b = b / 255;

	max = Math.max(r, g, b);
	min = Math.min(r, g, b);

	l = (min + max) / 2;

	diff = max - min;

	if (diff == 0) {
		s = 0;
		h = 0;
	} else {
		if(l > 0.5) {
			s = (diff) / (2 - min - max)
		} else {
			s = diff / (max + min)
		}

		switch(max) {
			case r:
				h = (g - b) / diff + (g < b ? 6 : 0);
				break;
			case g:
				h = (b - r) / diff + 2;
				break;
			case b:
				h = (r - g) / diff + 4;
				break;
		}
	}

	return {h: Math.round(h * 60), s: Math.round(s * 100), l: Math.round(l * 100)};
}

/**
 * Takes HSL values (H between 0 and 360, S and L each between 0 and 100) and returns the corresponding RGB values (each between 0 and 255)
 * Based on pseudo-code in the W3 Color Model document (http://www.w3.org/TR/2011/REC-css3-color-20110607/#hsl-color)
 */
collection.hslToRgb = function (h, s, l) {
	var m1, m2, m3, r, g, b;

	h = h / 360;
	s = s / 100;
	l = l / 100;

	m2 = l <= 0.5 ? l * (s + 1) : l + s - l * s;

	m1 = l * 2 - m2;

	r = collection.hueToRgb(m1, m2, h + 1/3);
	g = collection.hueToRgb(m1, m2, h);
	b = collection.hueToRgb(m1, m2, h - 1/3);

	return {r: Math.round(r * 255), g: Math.round(g * 255), b: Math.round(b * 255)};
}

collection.hueToRgb = function (m1, m2, h) {
	if(h < 0) {
		h = h + 1;
	} else if(h > 1) {
		h = h - 1;
	}

	if(h*6 < 1) {
		return m1 + (m2 - m1) * h * 6;
	} else if(h*2 < 1) {
		return m2;
	} else if(h*3 < 2) {
		return m1 + (m2 - m1) * (2/3 - h) * 6
	}

	return m1;
}

/**
 * Darkens the given color by the given amount (a number between 0 and 100)
 */
collection.darken = function (color, amount) {
	return collection._adjustLightness(color, -amount);
}

/**
 * Lightens the given color by the given amount (a number between 0 and 100)
 */
collection.lighten = function (color, amount) {
	return collection._adjustLightness(color, amount);
}

/**
 * Offsets the hue of a color by the given amount (in degrees)
 */
collection.spin = function (color, amount) {
	var hsl = collection.rgbToHsl(color.r, color.g, color.b);
	newH = (hsl.h + amount) % 360;
	hsl.h = (newH >= 0 ? newH : newH + 360);

	rgb = collection.hslToRgb(hsl.h, hsl.s, hsl.l);

	return Color.fromString("rgba(" + rgb.r + "," + rgb.g + "," + rgb.b + "," + color.a + ")");
}

/**
 * Changes the lightness of the given color by the given amount (a number between -100 and 100)
 */
collection._adjustLightness = function (color, amount) {
	var hsl = collection.rgbToHsl(color.r, color.g, color.b);

	hsl.l = Math.min(Math.max(hsl.l + amount, 0), 100);
	rgb = collection.hslToRgb(hsl.h, hsl.s, hsl.l);

	return Color.fromString("rgba(" + rgb.r + "," + rgb.g + "," + rgb.b + "," + color.a + ")");
}

collection.setCssTextAlignment = function(css, h, v) {
	switch (parseInt(h, 10)) {
		case 0:
			css.set("text-align", "left");
			break;
		case 1:
			css.set("text-align", "center");
			break;
		case 2:
			css.set("text-align", "right");
			break;
	}

	switch (parseInt(v, 10)) {
		case 0:
			css.set("vertical-align", "top");
			break;
		case 1:
			css.set("vertical-align", "middle");
			break;
		case 2:
			css.set("vertical-align", "bottom");
			break;
	}
}

