switch ($position.value) {
	case "middle":
		tl = tr = br = bl = 0;
		break;
	case "left":
		tl = bl = r;
		tr = br = 0;
		break;
	case "right":
		tr = br = r;
		tl = bl =0;
		break;
	case "top":
		tl = tr = r;
		bl = br = 0;
		break;
	case "bottom":
		tl = tr = 0;
		bl = br = r;
		break;
	default:
		tl = tr = br =bl = r;
		break;
}

"M " + tl + ",0 L " + ($box.w - tr) + ",0 a " + tr + "," + tr + " 0 0,1 " + tr + "," + tr
