For.DomContent:


var items = $contentText.value.split(/[\r\n]+/);

var specs = [];

for (var i = 0; i < items.length; i++) {
	var text = items[i];

	var css = new CSS();

	if (text.match(/^--+$/)) {	// Divider line
		text = "";
		css.set("background", $strokeColor.toRGBAString());
		css.set("height", "1px");
		css.set("margin", "9px 0");
	} else {
		css.set("padding", "4px 20px");
		css.set("font", $textFont.toCSSFontString());

		if (text.match(/^_(.*)/)) {	// Disabled item
			text = RegExp.$1;
			css.set("color", collection.lighten($textColor, 28).toRGBString());
		} else if (text.match(/^#(.*)/)) {	// Header item
			text = RegExp.$1;
			css.set("color", collection.lighten($textColor, 28).toRGBString());
			css.set("font-size", ($textFont.getPixelHeight() * 0.86) + "px");
		} else if (text.match(/(.*)\*$/)) {	// Selected item
			text = RegExp.$1;
			css.set("background", $selectedColor.toRGBString());
		}
	}

	specs.push({
		_name: "li",
		_uri: "http://www.w3.org/1999/xhtml",
		_text: text,
		style: css
	});
}

Dom.newDOMFragment(specs);

