For.DomContent:


var rows = $contentText.value.split(/[\r\n]+/);

var specs = [];
var header = false;

for (var i = 0; i < rows.length; i++) {
	var columns = rows[i].split(/\|/);

	header = (i == 0) && $header.value;
	var rowCss = new CSS();

	var row = {
		_name: "tr",
		_uri: "http://www.w3.org/1999/xhtml",
		style: rowCss,
		_children: []
	};

	if ($striped.value && i % 2 != 0) {
		rowCss.set("background", $stripeColor.toRGBString());
	}

	for (var j = 0; j < columns.length; j++) {
		var css = new CSS();
		var text = columns[j];

		css.set("padding", $condensed.value ? "4px" : "8px");
		collection.setCssTextAlignment(css, $textAlign.h, $textAlign.v);

		if ($bordered.value) {
			css.set("border", $strokeStyle.w + "px solid " + $strokeColor.toRGBString());
		}

		if (header) {
			css.set("border-bottom", $strokeStyle.w * 2 + "px solid " + $strokeColor.toRGBString());
			css.set("font-weight", "bold");
		}

		if (i > 0 || $topBorder.value) {
			css.set("border-top", $strokeStyle.w + "px solid " + $strokeColor.toRGBString());
		}

		var cell = {
			_name: header ? "th" : "td",
			_uri: "http://www.w3.org/1999/xhtml",
			style: css
		}

		if($useHtmlContent.value) {
			cell._html = text
		} else {
			cell._text = text
		}

		row._children.push(cell);
	}

	specs.push(row);
}

Dom.newDOMFragment(specs);

Action.Impl:
var b = this.getBounding();
var padding = this.getProperty("padding").x;

return [
	new SnappingData("InnerLeft", b.x + padding, "Left", true, this.id),
	new SnappingData("InnerRight", b.x + b.width - padding, "Right", true, this.id),
	new SnappingData("InnerTop", b.y + padding, "Top", false, this.id),
	new SnappingData("InnerBottom", b.y + b.height - padding, "Bottom", false, this.id)
];

For.DomContent:
var items = $textContent.value.split(/ +\/ +/);
var specs = [];

var css = new CSS();
css.set("font-size",$textFont.size);
css.set("font-family",$textFont.family);
css.set("font-style",$textFont.style);
css.set("font-weight",$textFont.weight);
css.set("font-decor",$textFont.decor);

var dividerCss = CSS.fromString(css.toString());
dividerCss.set("fill", collection.lighten($textColor, 30).toRGBString());

for (var i = 0; i < items.length; i++) {
	var css = new CSS();

	var crumb = items[i];

	if(crumb.match(/\S/) != null) {
		var color = $linkColor;

		if (i == items.length - 1) {
			color = $textColor;
		}

		css.set("fill", color.toRGBString());

		specs.push({
			_name: "tspan",
			_uri: "http://www.w3.org/2000/svg",
			_text : crumb,
			dx: (i == 0 ? 0 : 5),
			style: css
		});

		// slash dividers
		if (i != items.length - 1) {
			specs.push({
				_name: "tspan",
				_uri: "http://www.w3.org/2000/svg",
				_text : " / ",
				dx: 5,
				style: dividerCss
			});
		}
	}
}
Dom.newDOMFragment(specs);
